import { auth, googleProvider } from "../config/firebase";
import {
  createUserWithEmailAndPassword,
  signInWithPopup,
  signOut,
  onAuthStateChanged,
} from "firebase/auth";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

export const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null); // Track the authenticated user
  const navigate = useNavigate();

  // Check if user is authenticated and update user state
  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (user) => {
      setUser(user);
    });
    return unsubscribe; // Cleanup on component unmount
  }, []);

  // Redirect to the appropriate page if the user is authenticated
  useEffect(() => {
    if (user !== null) {
      // Wait until user state is fully updated before redirecting
      const isNewUser = user.metadata.creationTime === user.metadata.lastSignInTime;

      if (isNewUser) {
        navigate("/AddPerson"); // Redirect to 'AddPerson' if it's a new user
      } else {
        navigate("/dashboard"); // Redirect to 'dashboard' if it's an existing user
      }
    }
  }, [user, navigate]);

  const signIn = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      // Redirection is handled in useEffect
    } catch (err) {
      console.error("Error during sign-in:", err.message);
    }
  };

  const signInWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      // Redirection is handled in useEffect
    } catch (err) {
      console.error("Error during Google sign-in:", err.message);
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      navigate("/auth"); // Redirect to the Auth page after logout
    } catch (err) {
      console.error("Error during logout:", err.message);
    }
  };

  return (
    <div>
      <input
        placeholder="Email..."
        onChange={(e) => setEmail(e.target.value)}
      />
      <input
        placeholder="Password..."
        type="password"
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={signIn}>Sign In</button>
      <button onClick={signInWithGoogle}>Sign In With Google</button>
      <button onClick={logout}>Logout</button>
    </div>
  );
};
