import { useState } from "react";
import { doc, getDoc, updateDoc } from "firebase/firestore";
import { db } from "../config/firebase";
import { useNavigate } from "react-router-dom";
export default function UpdatePerson() {
  const [email, setEmail] = useState("");
  const [person, setPerson] = useState(null);

  const fetchPerson = async () => {
    try {
      const docRef = doc(db, "Person", email);
      const docSnap = await getDoc(docRef);
      if (docSnap.exists()) {
        setPerson(docSnap.data());
      } else {
        alert("No such person found!");
      }
    } catch (error) {
      console.error("Error fetching document:", error.message);
    }
  };

  const handleUpdate = async () => {
    try {
      const docRef = doc(db, "Person", email);
      await updateDoc(docRef, person);
      alert("Details updated successfully");
    } catch (error) {
      console.error("Error updating document:", error.message);
    }
  };

  return (
    <div>
      <h2>Update Person</h2>
      <input
        type="email"
        placeholder="Enter Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <button onClick={fetchPerson}>Fetch Details</button>
      {person && (
        <div>
          <textarea
            rows="10"
            value={JSON.stringify(person, null, 2)}
            onChange={(e) => setPerson(JSON.parse(e.target.value))}
          ></textarea>
          <button onClick={handleUpdate}>Update</button>
        </div>
      )}
    </div>
  );
}
